(2, 'Contact Form - Side', 'Name: {{var data.name}}\r\nE-mail: {{var data.email}}\r\n\r\nMessage: {{var data.message}}\r\nURL:  {{var data.url}}', NULL, 1, 'Contact Form - Side', NULL, NULL, '2013-07-25 23:40:47', '2013-07-25 23:43:47', 'contacts_email_email_template', '{\"var data.name\":\"Sender Name\",\"var data.email\":\"Sender Email\",\"var data.telephone\":\"Sender Telephone\",\"var data.comment\":\"Comment\"}'),
(3, 'Order1', '<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n<tr>\r\n    <td align=\"center\" valign=\"top\">\r\n        <!-- [ header starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n            <tr>\r\n                <td valign=\"top\"><a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"{{var store.getFrontendName()}}\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n            </tr>\r\n        </table>\r\n        <!-- [ middle starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n            <tr>\r\n                <td valign=\"top\">\r\n                    <p>\r\n                        <strong>Hello {{htmlescape var=$order.getCustomerName()}}</strong>,<br/>\r\n                        Thank you for your order from {{var store.getFrontendName()}}.\r\n                        Once your package ships we will send an email with a link to track your order.\r\n                        You can check the status of your order by <a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">logging into your account</a>.\r\n                        If you have any questions about your order please contact us at <a href=\"mailto:{{config path=\'trans_email/ident_support/email\'}}\" style=\"color:#1E7EC8;\">{{config path=\'trans_email/ident_support/email\'}}</a> or call us at <span class=\"nobr\">{{config path=\'general/store_information/phone\'}}</span>.\r\n                    </p>\r\n                    <p>Your order confirmation is below. Thank you again for your business.</p>\r\n\r\n                    <h3 style=\"border-bottom:2px solid #eee; font-size:1.05em; padding-bottom:1px; \">Your Order #{{var order.increment_id}} <small>(placed on {{var order.getCreatedAtFormated(\'long\')}})</small></h3>\r\n                    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n                        <thead>\r\n                        <tr>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">Billing Information:</th>\r\n                            <th width=\"3%\"></th>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">Payment Method:</th>\r\n                        </tr>\r\n                        </thead>\r\n                        <tbody>\r\n                        <tr>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getBillingAddress().format(\'html\')}}\r\n                            </td>\r\n                            <td>&nbsp;</td>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var payment_html}}\r\n                            </td>\r\n                        </tr>\r\n                        </tbody>\r\n                    </table>\r\n                    <br/>\r\n                    {{depend order.getIsNotVirtual()}}\r\n                    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n                        <thead>\r\n                        <tr>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">Shipping Information:</th>\r\n                            <th width=\"3%\"></th>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">Shipping Method:</th>\r\n                        </tr>\r\n                        </thead>\r\n                        <tbody>\r\n                        <tr>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getShippingAddress().format(\'html\')}}\r\n                                &nbsp;\r\n                            </td>\r\n                            <td>&nbsp;</td>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getShippingDescription()}}\r\n                                &nbsp;\r\n                            </td>\r\n                        </tr>\r\n                        </tbody>\r\n                    </table>\r\n                    <br/>\r\n                    {{/depend}}\r\n                    {{layout handle=\"sales_email_order_items\" order=$order}}\r\n                    <br/>\r\n                    {{var order.getEmailCustomerNote()}}\r\n                    <p>Thank you again,<br/><strong>{{var store.getFrontendName()}}</strong></p>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</div>', 'body,td { color:#2f2f2f; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; }', 2, '{{var store.getFrontendName()}}: New Order # {{var order.increment_id}}', NULL, NULL, '2016-06-13 20:00:17', '2016-06-13 20:00:17', 'sales_email_order_template', '{\"store url=\\\"\\\"\":\"Store Url\",\"skin url=\\\"images/logo_email.gif\\\" _area=\'frontend\'\":\"Email Logo Image\",\"htmlescape var=$order.getCustomerName()\":\"Customer Name\",\"var store.getFrontendName()\":\"Store Name\",\"store url=\\\"customer/account/\\\"\":\"Customer Account Url\",\"var order.increment_id\":\"Order Id\",\"var order.getCreatedAtFormated(\'long\')\":\"Order Created At (datetime)\",\"var order.getBillingAddress().format(\'html\')\":\"Billing Address\",\"var payment_html\":\"Payment Details\",\"var order.getShippingAddress().format(\'html\')\":\"Shipping Address\",\"var order.getShippingDescription()\":\"Shipping Description\",\"layout handle=\\\"sales_email_order_items\\\" order=$order\":\"Order Items Grid\",\"var order.getEmailCustomerNote()\":\"Email Order Note\"}'),
